sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	if(g_form.getValue('impact')=='1'){

	g_form.removeDecoration('impact','icon-star','','color-yellow');
		g_form.removeDecoration('impact','icon-star','','color-green');
		g_form.addDecoration('impact','icon-star','','color-red');
	}
	if(g_form.getValue('impact')=='2'){

	g_form.removeDecoration('impact','icon-star','','color-red');
		g_form.removeDecoration('impact','icon-star','','color-green');
		g_form.addDecoration('impact','icon-star','','color-yellow');
	}
	if(g_form.getValue('impact')=='3'){

	g_form.removeDecoration('impact','icon-star','','color-yellow');
		g_form.removeDecoration('impact','icon-star','','color-red');
		g_form.addDecoration('impact','icon-star','','color-green');
	}
}
